<?phpnamespace DixonsCz\Chuck\Jira;class Client implements IClient{        /**     *     * @var IConfiguration     */    protected $configuration;        /**     *     * @var DixonsCz\Chuck\Jira\IHttpRequest     */    protected $request;        /**     *      * @param IConfiguration $config     * @param IHttpRequest $request     */    public function __construct(IConfiguration $config, IHttpRequest $request)    {        $this->configuration = $config;        $this->request = $request;    }        /**     *      * @param string $path     * @return IResponse     */    public function requestPath($path)    {        $requestUrl = $this->configuration->getApiUrl() . $path;        $responseBody = $this->request->getAuthorizationResponse($requestUrl, $this->configuration->getUsername(), $this->configuration->getPassword());        return new Response($responseBody);    }}